INSERT INTO `jieqi_system_blocks` (`bid`, `blockname`, `modname`, `filename`, `classname`, `side`, `title`, `description`, `content`, `vars`, `template`, `cachetime`, `contenttype`, `weight`, `showstatus`, `custom`, `canedit`, `publish`, `hasvars`) VALUES(1, '本站公告', 'system', '', 'BlockSystemCustom', 4, '本站公告', '', '<br>\r\n欢迎使用 JIEQI CMS，安装完成后请删除install目录，然后进入后台进行参数、用户组、权限和区块的相关设置。\r\n<br><br>\r\n更多程序和使用方面咨询请访问：\r\n<br><br>\r\n<a href="http://www.jieqi.com" target="_blank">http://www.jieqi.com</a>\r\n<br><br>', '', '', 0, 1, 10000, 0, 1, 1, 3, 0);
