INSERT INTO `jieqi_system_configs` VALUES ('29', 'system', 'mailtype', '邮件发送方式', '0', '', '0', '7', 'a:4:{i:0;s:14:\"不发送任何邮件\";i:1;s:40:\"通过 PHP 函数及 UNIX sendmail 发送(推荐)\";i:2;s:49:\"通过 SOCKET 连接 SMTP 服务器发送(支持 ESMTP 验证)\";i:3;s:60:\"通过 PHP 函数 SMTP 发送 Email(仅 win32 下有效, 不支持 ESMTP)\";}', '30100', '邮件设置');
INSERT INTO `jieqi_system_configs` VALUES ('30', 'system', 'maildelimiter', '邮件头的分隔符', '1', '', '0', '7', 'a:2:{i:0;s:18:\"使用 LF 作为分隔符\";i:1;s:20:\"使用 CRLF 作为分隔符\";}', '30200', '邮件设置');
