INSERT INTO `jieqi_system_configs` VALUES ('33', 'system', 'mailauth', '是否需要 AUTH LOGIN 验证', '1', '', '0', '9', 'a:2:{i:1;s:2:\"是\";i:0;s:2:\"否\";}', '30500', '邮件设置');
INSERT INTO `jieqi_system_configs` VALUES ('34', 'system', 'mailfrom', '发信人地址', '', '如果需要验证,必须为本服务器地址。地址写法可以是 webmaster@jieqi.com 或者 JieqiCMS <webmaster@jieqi.com>', '0', '1', '', '30500', '邮件设置');
