INSERT INTO `jieqi_system_configs` VALUES ('36', 'system', 'mailpassword', '验证密码', '', 'SMTP 邮件服务器密码', '0', '1', '', '30700', '邮件设置');
INSERT INTO `jieqi_system_configs` VALUES ('37', 'system', 'posttitlemax', '发帖标题最多几个字节', '60', '跟数据结构中的字段长度有关，一般不用修改', '0', '3', '', '40100', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('38', 'system', 'postintervaltime', '两次发贴最少间隔时间', '0', '单位是秒，设置成 0 表示无时间间隔限制', '0', '3', '', '40200', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('39', 'system', 'postdenywords', '禁止发表的词语', '', '禁止发表的词语每条一行，词语中可以使用“*”表示任意长字符串，或者使用“?”表示任意一个字节字符。如“a*d”可匹配“abd”、“abcd”，而“a?d”则匹配“abc”、“acd”', '0', '2', '', '40300', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('40', 'system', 'postreplacewords', '发帖内容替换', '', '替换规则每条一行，写法为：“from=to”表示内容“from”将被替换成“to”。也可以只写“from”，这样内容中的“from”会替换成“**”，相当于隐藏关键词效果。', '0', '2', '', '40400', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('41', 'system', 'postminsize', '发帖内容最少字节数', '0', '0 表示不限制', '0', '3', '', '40500', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('42', 'system', 'postmaxsize', '发帖内容最多字节数', '0', '0 表示不限制', '0', '3', '', '40600', '内容检查设置');
INSERT INTO `jieqi_system_configs` VALUES ('43', 'system', 'postdenyrubbish', '禁止灌水帖子', '0', '被怀疑是灌水的帖子将禁止发表，程序判断不一定准确，请慎用', '0', '9', 'a:2:{i:1;s:2:\"是\";i:0;s:2:\"否\";}', '40700', '内容检查设置');
