INSERT INTO `jieqi_system_configs` VALUES ('48', 'system', 'JIEQI_USER_ENTRY', '用户入口服务器网址', '', '在使用多服务器情况下设置用户注册、登录、退出等功能的服务器网址（如：http://www.domain.com），后面不带斜线，单服务器可以留空', '1', '1', '', '10530', '网站基本信息');
