INSERT INTO `jieqi_system_configs` VALUES ('50', 'system', 'JIEQI_META_DESCRIPTION', '网站描述', '基于杰奇小说连载系统构建，为您提供提供内容优质，更新最快的小说。', '', '1', '2', '', '10700', '网站基本信息');
